mapper.select:
SELECT re.id, re.name, re.parent_id, re.url, re.permission, re.icon, node.id AS node_id, node.name AS node_name, node.type AS node_type, node.url AS node_url, node.parent_id AS node_parent_id, node.permission AS node_permission, node.icon AS node_icon FROM resource re LEFT JOIN sys_role_resources rr ON re.id = rr.resources_id LEFT JOIN sys_user u ON rr.role_id = u.role_id LEFT JOIN resource node ON node.parent_id = re.id and node.type=#{type} WHERE (re.parent_id = '0' OR re.parent_id IS NULL )
AND u.id = #{userId}
AND re.type= #{type}
ORDER BY re.sort ASC, node.sort ASC
